let fetchAllUsers = () => CommonActions.baseAsyncThunk(
	RestAPI.user.fetchAllUsers(),
	FETCH_ALL_USERS,
	(res, body, hasError) => {
        let messages = [];
        let data = null;
		if (hasError && body) {
            messages = body.messages;
			Notifications.addNotification(messages);

		} else if (body && body.messages) {
			Notifications.addNotification(body.messages);
            messages = body.messages;
            data = body.data;

        }

        return data;
	});

export default {
	fetchAllUsers
};
