import { FETCH_ALL_USERS } from "../../common/redux/RATypes";
import RestAPI from "../../common/RestAPI";
import CommonActions from "../../common/redux/CommonActions";
import Notifications from "../../common/ui/connected/Notifications/Notifications";

let fetchAllUsers = () => CommonActions.baseAsyncThunk(
	RestAPI.user.fetchAllUsers(),
	FETCH_ALL_USERS,
	(body, hasError) => {
        let messages = [];
        let data = null;
		if (hasError && body) {
            messages = body.messages;
			Notifications.addNotification(messages);

		} else if (body && body.messages) {
			Notifications.addNotification(body.messages);
            messages = body.messages;
            data = body.data;
        }
        console.log('data', data);
        return data;
	});

export default {
	fetchAllUsers
};
