import { FETCH_ALL_USERS } from "../../common/redux/RATypes";
import RestAPI from "../../common/RestAPI";

let fetchlAllUsers = () => BaseActions.baseAsyncThunk(
    RestAPI.user.fetchAll(),
    FETCH_ALL_USERS,
    (res, body, hasError) => {
        let messages = [];
        let data = null;
        if (hasError && body) {
            messages = body.messages;
            Notifications.addNotification(messages);
        } else if (body && body.messages) {
            messages = body.messages;
            data = body.data;
        }

        return data;
    }
);

export default {
	fetchlAllUsers
};
