import React, {Component, Fragment} from "react";
import "./Auth.css";
import PublicTemplate from "../../common/ui/PublicTemplate/PublicTemplate";
import {connect} from "react-redux";
import AuthActions from "./AuthActions";
import Utils from "../../common/Utils";
import Constants from "../../common/Constants";
import {Link, Redirect} from "react-router-dom";

const mapReduxStateToProps = reduxState => ({
	loggedIn: reduxState.auth.loggedIn,
	registeredSuccessfully: reduxState.auth.registeredSuccessfully,
});

const mapReduxDispatchToProps = {
	register: AuthActions.register,
};

export default connect(mapReduxStateToProps, mapReduxDispatchToProps)(
	class RegisterView extends Component {
		constructor(props) {
			super(props);

			this.state = {
				form: {
					firstName: "Rodrigo",
					surname: "Tessarollo Neves",
					cpf: "02687403130",
					email: "rodrigotessarollo@hotmail.com",
					password: "rodrigo",
					passwordConfirm: "rodrigo",
				}
			};
		}

		/*
		 * Custom actions
		 */

		isFormValid() {
			return !Utils.isEmpty(this.state.form.firstName)
				&& !Utils.isEmpty(this.state.form.surname)
				&& !Utils.isEmpty(this.state.form.cpf)
				&& this.state.form.cpf.isCPF()
				&& !Utils.isEmpty(this.state.form.email)
				&& this.state.form.email.isEmail()
				&& !Utils.isEmpty(this.state.form.password)
				&& !Utils.isEmpty(this.state.form.passwordConfirm)
				&& this.state.form.password === this.state.form.passwordConfirm;
		}

		handleChange = event => {
			this.setState({
				form: {...this.state.form, [event.target.id]: event.target.value.unmask()}
			});
		};

		handleSubmit = async event => {
			event.preventDefault();
			this.props.register(this.state.form);
		};


		/*
		 * React life cycles
		 */

		render() {
			return (
				<Fragment>
					<div className="container" style={{paddingBottom: 40}}>
						<div className="login-wrap">
							<div className="login-content">
								<div className="login-logo">
									<Link to={"/"}>
										<img src={require("./../../assets/images/logo.png")} alt="appname"/>
									</Link>
								</div>
								<div className="login-form">
									<form action="" method="post" onSubmit={this.handleSubmit}>
										<div className="form-group">
											<label>Nome</label>
											<input className="au-input au-input--full"
											       type="text"
											       required={true}
											       maxLength={50}
											       id="firstName"
											       name="firstName"
											       value={this.state.form.firstName}
											       onChange={this.handleChange}
											       placeholder="Nome"/>
										</div>
										<div className="form-group">
											<label>Sobrenome</label>
											<input className="au-input au-input--full"
											       type="text"
											       maxLength={100}
											       required={true}
											       id="surname"
											       name="surname"
											       value={this.state.form.surname}
											       onChange={this.handleChange}
											       placeholder="Sobrenome"/>
										</div>
										<div className="form-group">
											<label>CPF</label>
											<input className="au-input au-input--full"
											       type="text"
											       required={true}
											       maxLength={14}
											       id="cpf"
											       name="cpf"
											       value={this.state.form.cpf.maskCPF()}
											       onChange={this.handleChange}
											       placeholder="CPF"/>
										</div>
										<div className="form-group">
											<label>Email</label>
											<input className="au-input au-input--full"
											       type="email"
											       maxLength={100}
											       required={true}
											       id="email"
											       name="email"
											       value={this.state.form.email}
											       onChange={this.handleChange}
											       placeholder="Email"/>
										</div>
										<div className="form-group">
											<label>Senha</label>
											<input className="au-input au-input--full"
											       type="password"
											       required={true}
											       maxLength={100}
											       id="password"
											       name="password"
											       value={this.state.form.password}
											       onChange={this.handleChange}
											       placeholder="Senha"/>
										</div>
										<div className="form-group">
											<label>Senha</label>
											<input className="au-input au-input--full"
											       type="password"
											       required={true}
											       maxLength={100}
											       id="passwordConfirm"
											       name="passwordConfirm"
											       value={this.state.form.passwordConfirm}
											       onChange={this.handleChange}
											       placeholder="Senha"/>
										</div>

										<div className="login-checkbox">
											<label>
												<Link to={Constants.ROUTES.FORGOT}>Esqueceu sua senha?</Link>
											</label>
										</div>
										<button className="au-btn au-btn--block au-btn--green m-b-20"
										        disabled={!this.isFormValid()}
										        type="submit">Criar conta
										</button>
									</form>
									<div className="register-link">
										<p>Já tem uma conta? <Link to={Constants.ROUTES.LOGIN}>Faça o Login</Link>
										</p>
									</div>
								</div>
							</div>
						</div>
					</div>


					{/*REDIRECT TO HOME IF USER ARE LOGGED IN*/}
					{this.props.loggedIn &&
					<Redirect to={Constants.ROUTES.HOME}/>
					}

					{/*REDIRECT TO LOGIN IF USER ARE CREATED IN*/}
					{this.props.registeredSuccessfully &&
					<Redirect to={Constants.ROUTES.LOGIN}/>
					}
				</Fragment>
			);
		}
	}
);
