import React, {Component} from "react";
import SecureTemplate from "../../common/ui/SecureTemplate/SecureTemplate";
import {connect} from "react-redux";
import CrudActions from "./CrudActions";

const mapReduxStateToProps = reduxState => ({
	users: reduxState.users
});

const mapReduxDispatchToProps = {
	fetchAll: CrudActions.fetchAllUsers,
};

export default connect(mapReduxStateToProps, mapReduxDispatchToProps)( class CrudHomeView extends Component{
	constructor(props) {
		super(props);

		this.state = {};
		this.props.fetchAll({});
	}

	async componentDidMount() {
	}

	render() {

		const headerStyle = {color: "#626262", fontSize: "14px", opacity: "0.6"}

		return (
			<SecureTemplate>
				<div className="main-content">
					<div className="section__content section__content--p30">
						<div className="container-fluid">
							<div className="row">
								<div className="col-md-12">
									<div className="overview-wrap">
										<h2 className="title-1">Usuarios</h2>
									</div>
								</div>
							</div>

								<div className="row p-4">
								<table className="table table-striped">
									<thead>
										<tr>
											<th scope="col" className="text-center" style={headerStyle}>Nome</th>
											<th scope="col" className="text-center" style={headerStyle}>CPF</th>
											<th scope="col" className="text-center" style={headerStyle}>E-mail</th>
											<th scope="col" className="text-center" style={headerStyle}>Endereco</th>
										</tr>
									</thead>
									<tbody>
									{this.props.users && this.props.users.content && this.props.users.content.map((user, i) => (
										<tr key={i}>
											<td style={bodyStyle} className="text-center">{user.name}</td>
											<td style={bodyStyle} className="text-center">{user.cpf}</td>
											<td style={bodyStyle} className="text-center">{user.email}</td>
											<td style={bodyStyle} className="text-center"></td>
										</tr>
										))}

									</tbody>
								</table>

							</div>
							))}

						</div>
					</div>
				</div>
			</SecureTemplate>
		);
	}
}
);
